DROP TABLE IF EXISTS Job_Positions;
DROP TABLE IF EXISTS Roles;
DROP TABLE IF EXISTS Levels;
DROP TABLE IF EXISTS Employement_Relationships;
DROP TABLE IF EXISTS Income_Types;
DROP TABLE IF EXISTS Hierarchies;
DROP TABLE IF EXISTS public.user_type_menu_items;
DROP TABLE IF EXISTS public.menu_items;


-- Create the user_types table first, as it's referenced by the users table
CREATE TABLE IF NOT EXISTS user_types (
    id SERIAL PRIMARY KEY,
    type_name VARCHAR(50) UNIQUE NOT NULL
);

-- Insert some default user types (optional, but good for initial setup)
INSERT INTO user_types (type_name) VALUES
('Admin'),
('Standard'),
('Guest')
ON CONFLICT (type_name) DO NOTHING; -- Prevents errors if types already exist

-- Create the users table
CREATE TABLE IF NOT EXISTS users (
    id SERIAL PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    password VARCHAR(255) NOT NULL, -- Store hashed passwords (e.g., bcrypt hash)
    email VARCHAR(100) UNIQUE NOT NULL,
    user_type_id INTEGER NOT NULL,
    active BOOLEAN DEFAULT TRUE NOT NULL,
    deleted BOOLEAN DEFAULT FALSE NOT NULL,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,

    CONSTRAINT fk_user_type
        FOREIGN KEY (user_type_id)
        REFERENCES user_types (id)
        ON DELETE RESTRICT -- Prevent deleting user types if users are associated
);

-- Optional: Add indexes for performance on frequently queried columns
CREATE INDEX IF NOT EXISTS idx_users_username ON users (username);
CREATE INDEX IF NOT EXISTS idx_users_email ON users (email);
CREATE INDEX IF NOT EXISTS idx_users_user_type_id ON users (user_type_id);
CREATE INDEX IF NOT EXISTS idx_users_active ON users (active);
CREATE INDEX IF NOT EXISTS idx_users_deleted ON users (deleted);


CREATE TABLE IF NOT EXISTS public.menu_items
(
    id integer NOT NULL DEFAULT nextval('menu_items_id_seq'::regclass),
    menu_label character varying(64) COLLATE pg_catalog."default",
    menu_icon character varying(128) COLLATE pg_catalog."default",
    menu_alt character varying(128) COLLATE pg_catalog."default",
    menu_action character varying(128) COLLATE pg_catalog."default",
    parent_id integer,
    priority integer,
    active boolean DEFAULT true,
    deleted boolean DEFAULT false,
    CONSTRAINT menu_items_pkey PRIMARY KEY (id)
);

CREATE TABLE IF NOT EXISTS public.user_type_menu_items
(
    user_type_id integer,
    menu_item_id integer
);
	
CREATE TABLE Roles (
    ID SERIAL PRIMARY KEY,
    NAME VARCHAR(255) UNIQUE NOT NULL,
    DESCRIPTION VARCHAR(255)
);

CREATE TABLE Levels (
    ID SERIAL PRIMARY KEY,
    NAME VARCHAR(255) UNIQUE NOT NULL,
    DESCRIPTION VARCHAR(255)
);

CREATE TABLE Employement_Relationships (
    ID SERIAL PRIMARY KEY,
    NAME VARCHAR(255) UNIQUE NOT NULL,
    DESCRIPTION VARCHAR(255)
);

CREATE TABLE Income_Types (
    ID SERIAL PRIMARY KEY,
    NAME VARCHAR(255) UNIQUE NOT NULL,
    DESCRIPTION VARCHAR(255)
);

CREATE TABLE Hierarchies (
    ID SERIAL PRIMARY KEY,
    NAME VARCHAR(255) UNIQUE NOT NULL,
    DESCRIPTION VARCHAR(255),
    MIN_SALARY NUMERIC(10, 2),
    MAX_SALARY NUMERIC(10, 2),
    EXPERIENCE VARCHAR(255),
    PRIVILEGES VARCHAR(255)
);

CREATE TABLE Employees (
    ID INT PRIMARY KEY,
    NAME VARCHAR(255),
    LASTNAME VARCHAR(255),
    SEC_LASTNAME VARCHAR(255),
    USERNAME VARCHAR(255),
    EMAIL VARCHAR(255),
    TAX_ID VARCHAR(255),
    GOVERMENT_CODE VARCHAR(255),
    TRANSFER_BANK_NUMBER VARCHAR(255),
    BANK VARCHAR(255),
    ID_OPTA INT,
    ID_BANK VARCHAR(255),
    SOCIAL_SECURITY_NUMBER VARCHAR(255),
    EMPLOYEMENT_RELATIONSHIP_ID INT,
    INCOME_TYPE_ID INT,
    IMSS_BW_GROS_SALARY NUMERIC(10, 2),
    IMSS_BW_NET_SALARY NUMERIC(10, 2),
    GIN_BW_NET_SALARY NUMERIC(10, 2),
    MONTHLY_BONUS NUMERIC(10, 2),
    ASSIGNATION_BONUS NUMERIC(10, 2),
    TOTAL_MONTHLY_SALARY NUMERIC(10, 2),
    CONTRACTING_DATE DATE,
    BIRTH_DATE DATE,
    LAST_RISE_DATE DATE,
    ROLE_ID INT,
    LEVEL_ID INT,
    HIERARCHIE_ID INT,
    ACTIVE BOOLEAN,
    DELETED BOOLEAN,
    FOREIGN KEY (ROLE_ID) REFERENCES Roles(ID),
    FOREIGN KEY (LEVEL_ID) REFERENCES Levels(ID),
    FOREIGN KEY (HIERARCHIE_ID) REFERENCES Hierarchies(ID),
    FOREIGN KEY (EMPLOYEMENT_RELATIONSHIP_ID) REFERENCES Employement_Relationships(ID),
    FOREIGN KEY (INCOME_TYPE_ID) REFERENCES Income_Types(ID)
);

